import { formatTime } from "../helpers/helpers";

const LapRunner = ({ pos, laps }) => (
  <div>
    <label>Runner {pos}</label>
    <h2>{formatTime(time)}</h2>
    <button>Add Lap</button>
    <button>End Run</button>
    {laps.map((lap, i) => {
      const lapNumber = laps.length - 1;
      return (
        <div key={lapNumber}>
          <label className="lap__label">{this.props.label}</label>
          <h3 className="lap__clock">{formatTime(this.props.time)}</h3>
        </div>
      );
    })}
  </div>
);

export default LapRunner;
