import LapRunners from "../components/LapRunners";

export default class HomePage extends React.Component {
  state = {
    runners: null
  };

  setRunners = numOfRunners => {
    this.setState({ runners: numOfRunners });
  };

  resetRunners = () => {
    this.setState({ runners: null });
  };

  render() {
    return (
      <div>
        <LapRunners runners={this.state.runners} />
        <div>
          Welcome to the Multi-Runner Lap Time Tracker built in Next.js!
        </div>
      </div>
    );
  }
}

