import { formatTime } from "../helpers/helpers";
import Lap from "./Lap";

const LapRunner = ({ pos, laps, addLap }) => (
  <div>
    <label>Runner {pos}</label>
    <button onClick={addLap}>Add Lap</button>
    <button>End Run</button>
    {laps.map((lap, i) => {
      const lapNumber = laps.length - i;
      // label={`Lap #${lapNumber}`} time={lap}
      //
      return <Lap label={`Lap #${lapNumber}`} time={lap} />;
    })}
  </div>
);

export default LapRunner;
